import React, { useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { addLog } from '../../actions/logActions';
import M from 'materialize-css/dist/js/materialize.min.js';

const AddLogModal = ({ addLog }) => {
  const [message, setMessage] = useState('');
  const [attention, setAttention] = useState(false);
  const [tech, setTech] = useState('');

  const onSubmit = () => {
    if (message === '' || tech === '') {
      M.toast({ html: 'Please enter a message and tech' });
    } else {
      const newLog = {
        message, attention, tech, 
        date: new Date()
      }

      addLog(newLog);

      M.toast({html: `Log added by `});
    }

    // Clear fields
    setMessage('');
    setTech('');
    setAttention(false);
  };

  return (
    <div id="add-log-modal" className="modal" style={modalStyle}>
      <div className="modal-content">
        <h4>Enter System Log</h4>
        <div className="row">
          <div className="input-field">
            <input
              type="text"
              name="message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            ></input>
            <label htmlFor="message" className="active">
              Log Message
            </label>
          </div>
          <div className="row">
            <div className="input-field">
              <select
                name="tech"
                value={tech}
                className="browser-default"
                onChange={(e) => setTech(e.target.value)}
              >
                <option disable value="">
                  Select Technician
                </option>
                <option value="Seymore">Seymore</option>
                <option value="John Doe">John Doe</option>
                <option value="Gabbagool">Gabbagool</option>
              </select>
            </div>
          </div>
          <div className="row">
            <p>
              <label>
                <input
                  type="checkbox"
                  className="filled-in"
                  checked={attention}
                  value={attention}
                  onChange={(e) => setAttention(!attention)}
                ></input>
                <span>Needs Attention</span>
              </label>
            </p>
          </div>
        </div>
      </div>
      <footer className="modal-footer">
        <a
          href="#!"
          onClick={onSubmit}
          className="modal-close yellow darken-2 btn"
        >
          Submit
        </a>
      </footer>
    </div>
  );
};

AddLogModal.propType = {
  addLog: PropTypes.func.isRequired,
};

const modalStyle = {
  width: '75%',
  height: '75%',
};

export default connect(null, { addLog })(AddLogModal);
